### Steps to reproduce

```typescript
import { game } from 'cc';
import { _decorator, Component, sp } from 'cc';
const { ccclass, property } = _decorator;

@ccclass('Xtk')
export class Xtk extends Component {
    @property(sp.Skeleton) private skeleton: sp.Skeleton = null;
    protected start() {
        this.test([0.2, 0.5, 1, 2, 4, 8]);
    }

    private test(speeds: number[]) {
        let speed = speeds.shift();
        if (!speed) return;

        let t0 = game.frameStartTime;
        let animation = this.skeleton.findAnimation("Skill");
        this.skeleton.timeScale = speed;
        this.skeleton.setAnimationCacheMode(sp.AnimationCacheMode.REALTIME);
        this.skeleton.setCompleteListener(track => {
            console.log("\nspeed:", speed);
            console.log("expected time:", animation.duration / speed);
            console.log("frame time:", (game.frameStartTime - t0) / 1000);
            console.log("track time:", track.trackTime);
            this.test(speeds);
        });
        this.skeleton.setAnimation(0, animation.name, false);
    }
}

```

